query resultQuery(
  $paymentId: ID!
) {
  scenes {
    payment {
      checkStatus(paymentId: $paymentId) {
        status
        ...Confirmation_data
        ...Rejection_data
        id
      }
    }
  }
}

fragment Confirmation_data on Payment {
  total {
    ...Money_data
  }
}

fragment Rejection_data on Payment {
  total {
    ...Money_data
  }
}

fragment Money_data on Money {
  amount
  currency
}

fe6ede535e34fded4ff5ce6778b87baa:
query PaymentsListQuery(
  $clientId: ID!
) {
  scenes {
    dashboard {
      payments(clientId: $clientId) {
        id
        ...PaymentRow_data
      }
    }
  }
}

fragment PaymentRow_data on Payment {
  location
  date
  ...StatusIcon_data
  total {
    ...Money_data
  }
  retailer {
    name
    id
  }
}

fragment StatusIcon_data on Payment {
  status
}

fragment Money_data on Money {
  amount
  currency
}
